<span class="line"><span style="color: #EEFFFF">console</span><span style="color: #89DDFF">.</span><span style="color: #82AAFF">log</span><span style="color: #EEFFFF">(arr</span><span style="color: #89DDFF">.</span><span style="color: #82AAFF">join</span><span style="color: #EEFFFF">())</span><span style="color: #89DDFF">;</span></span>
